function Lexicon() {

    this.addAdjectives();
    this.addNouns();
}

Lexicon.prototype.random = function(type) {
    switch (type) {
        case 'jj':
        var txt = this.adjectiveDeck.draw();
        if (!txt) {
            this.adjectiveDeck = tombola.deck(this.adjectives.slice());
            txt = this.adjectiveDeck.draw();
        }
        return txt;
        break;

        case 'nn':
        var txt = this.nounDeck.draw();
        if (!txt) {
            this.nounDeck = tombola.deck(this.nouns.slice());
            txt = this.nounDeck.draw();
        }
        return txt;
        break;

        default:
        return tombola.item(tombola.item(this.adjectives, this.nouns));
        break;
    }
};




